import React from "react";

const projects = [
  {
    title: "Tic Tac Toe",
    image: "../images/TicTacToe.png",
    link: "https://asaggse.github.io/tic-tac-toe/"
  },
  {
    title: "Calculator",
    image: "../images/Calculator.png",
    link: "https://asaggse.github.io/calculator-react/"
  }
];

const renderProjects = projects => {
  return projects.map(project => {
    return (
      <div key={project.title} className="container">
        <h1 className="preview-title">{project.title}</h1>
        <div className="preview">
          <a href={project.link} target="_blank" rel="noreferrer">
            <img src={project.image} alt={project.title} className="my-project" />
          </a>
        </div>
      </div>
    );
  });
};

const Projects = () => {
  return (
    <div className="App-projects">
      <h1 className="section-title">Projects</h1>
      {renderProjects(projects)}
    </div>
  );
};

export default Projects;

